import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import Categories from "./components/Categories";



class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      orders: [],
      currenetItems: [],
      items: [
        {
          id: 1,
          title: 'BMW M5 F90',
          img: 'v5f90jpg.jpg',
          desc: 'Одно из лучших творений BMW.',
          category: 'Sport',
          price: '100000.00$',
          color_car: 'Черный'
        },
        {
          id: 2,
          title: 'Mersedes CLS 63 AMG',
          img: 'mersedesccls.jpg',
          desc: 'Стиль, элегантность, комфорт - CLS.',
          category: 'Sport',
          price: '150000.00$',
          color_car: 'Серый'
        },
        {
          id: 3,
          title: 'BMW 3 E36',
          img: 'BMWE36.jpg',
          desc: 'Легенда 90-х.',
          category: 'Legend',
          price: '1150000.00$',
          color_car: 'Белый'
        }
      ]
    }
    this.state.currenetItems = this.state.items
    this.addToOrder = this.addToOrder.bind(this)
    this.deleteOrder = this.deleteOrder.bind(this)
    this.chooseCategory = this.chooseCategory.bind(this)
  }
  render(){
    return (
      <div className="wrapper">
        <Header orders={this.state.orders} onDelete={this.deleteOrder}/>
        <Categories chooseCategory={this.chooseCategory}/>
        <Items items={this.state.currenetItems} onAdd={this.addToOrder}/>
        <Footer />
      </div>
    );
  }

  chooseCategory(category) {
    if(category === 'all')
    {
      this.setState({currenetItems: this.state.items})
      return
    }
    this.setState({
      currenetItems: this.state.items.filter(el => el.category === category)
    })
  }

  deleteOrder(id){
    this.setState({orders: this.state.orders.filter(el => el.id !== id)})
  }

  addToOrder(item){
    let isInArray = false
    this.state.orders.forEach(el => {
      if(el.id === item.id)
        isInArray = true
    })
    if(!isInArray)
      this.setState({orders: [...this.state.orders, item] })
  }
}

export default App;
