import React, { useState } from 'react'
import { FaShoppingCart } from "react-icons/fa";
import { FaAddressCard } from "react-icons/fa";



import Order from './Order';

const showOrders = (props) => {
  let summa = 0
  props.orders.forEach(el => summa += 1)
  return (<div>
    {props.orders.map(el => (
      <Order onDelete={props.onDelete} key={el.id} item={el} />
    ))}
    <p className='summa'>Количество машин: {summa}</p>
  </div>)
}

const showNothing = () => {
  return (<div className='empty'>
    <h2>Машин нет</h2>
  </div>)
}

export default function Header(props) {
  let [cartOpen, setCartOpen] = useState(false)
  let [polOpen, setpolOpen] = useState(false)
  return (
    <header>
        <div>
            <span className='logo'>Учет автомобилей</span>
            <ul className='nav'>
                <li>Про нас</li>
                <li>Контакты</li>
                <li>кабинет</li>
            </ul>
            <FaShoppingCart onClick={() => setCartOpen(cartOpen = !cartOpen)} className={`shop-cart-button ${cartOpen && 'active'}`} />
            {cartOpen && (
              <div className='shop-cart'>
                {props.orders.length > 0 ?
                  showOrders(props) : showNothing()}
              </div>
            )}
            <FaAddressCard onClick={() => setpolOpen(polOpen = !polOpen)} className={`shop-pol-button ${polOpen && 'active'}`} />
            {polOpen && (
              <div className='shop-pol'>
                <p>Имя</p>
                <input id="name" />
                <p>Логин</p>
                <input id="login" />
                <p>Пароль</p>
                <input id="password" />
                <button type="submit" id="submit">Зарегестрироваться</button>
              </div>
            )}
        </div>
        <div className='presentation'></div>
    </header>
  )
}

<script src="script.js"></script>
